.bot_image > div > div > div > div > div > div > div {
    animation: float 34s linear 0s infinite normal none;
    transform-origin: 50% 50%;
    z-index: 1 !important;
}

@keyframes float {
    0%, 100% {
        transform: translateX(0%) translateY(0%);
    }

    15% {
        transform: translateX(-30px) translateY(-30px) rotate(4deg);
    }

    30% {
        transform: translateX(15px) translateY(25px) rotate(-4deg);
    }

    45% {
        transform: translateX(-15px) translateY(-15px) rotate(-3.6deg);
    }

    60% {
        transform: translateX(9px) translateY(15px) rotate(-2.4deg);
    }

    75% {
        transform: translateX(-6px) translateY(-10px) rotate(1.2deg);
    }
}
