let avatars = global.get('avatars')

let filteredAvatars = avatars.filter(avatar => avatar.deleted === false);

filteredAvatars.sort((a, b) => b.createdAt - a.createdAt);

msg.payload = filteredAvatars;

return msg;

let avatars = global.get('avatars');

// Assuming msg.target contains the _id of the avatar to mark as selected
let targetAvatarId = (msg.payload._id).toString();

// Loop through the avatars array
avatars.forEach(avatar => {
    // Set selected to true only for the target avatar, and false for others
    avatar.selected = (avatar.presenter_id).toString() === targetAvatarId;
});

// Update msg.payload to indicate the change
msg.payload = {
    success: true,
    message: `Avatar with ID ${targetAvatarId} marked as selected, others deselected`
};

// Set the updated avatars array back to the global context
global.set('avatars', avatars);

return msg;

